function general_script(){$(function(){setTimeout(function(){$(".alert").fadeOut(500)},5e3)}),$("#btn-print").click(function(){window.print()}),$("#btn-savepdf").click(function(){var t=new jsPDF,e=$("table").clone();e.find("tr th.hidden-pdf, tr td.hidden-pdf").remove();var n=t.autoTableHtmlToJson(e.get(0));t.autoTable(n.columns,n.data,{theme:"grid",styles:{fontSize:9},margin:{top:55}}),t.save("sample.pdf")}),$("form").submit(function(){$("button").attr("type","button")})}function overlay(t){$("#overlay").show(),$("#"+t).addClass("overlay-active"),$("html").click(function(){$("#overlay").hide(),$("#"+t).removeClass("overlay-active")})}